var TodoCheckbox = React.createClass({displayName: "TodoCheckbox",
	getInitialState: function() {
		return { checked: false };
	},
	onClick: function() {
		this.setState({ checked: !this.state.checked });
		this.props.update();
	},
	render: function() {
		var icon = this.state.checked? "check" : "unchecked";

		return(
			React.createElement("span", {onClick:  this.onClick}, 
				React.createElement("span", {className:  "glyphicon glyphicon-" + icon, "aria-hidden": "true"})
			)
		);
	}
});

var TodoText = React.createClass({displayName: "TodoText",
	render: function() {
		return React.createElement(this.props.checked? "del" : "span", null, " " + this.props.children + " ");
	}
});

var TodoRemove = React.createClass({displayName: "TodoRemove",
	render: function() {
		return(
			React.createElement("div", null)
		);
	}
});

var TodoHolder = React.createClass({displayName: "TodoHolder",
	getInitialState: function() {
		return { checked: false };
	},
	onCheck: function() {
		this.setState({ checked: !this.state.checked });
	},
	render: function() {
		return(
			React.createElement("div", null, 
				React.createElement(TodoCheckbox, {update:  this.onCheck.bind(this) }), 
				React.createElement(TodoText, {checked:  this.state.checked},  this.props.children)
			)
		);
	}
});

var Container = React.createClass({displayName: "Container",
	render: function() {
		return(
			React.createElement("div", {className: "well my-holder"}, 
				React.createElement("div", {className: "panel panel-default"}, 
					React.createElement("div", {className: "panel-body"}, 
						React.createElement(TodoHolder, null, "Hello")
					)
				)
			)
		);
	}
});

React.render(
	React.createElement(Container, null),
	document.getElementById('app')
);