var isTouch = 'ontouchstart' in document.documentElement;

var TodoProto = {
    get completion() {
        var total = this.children.length * 100;
        var done = 0;

        this.children.forEach(function(item) {
            done += item.completion;
        });

        return total ? Math.floor(done / total * 100) : 0;
    },
    insert: function(text, val) {
        var newItem = Object.create(TodoProto, {
            text: { value: text },
            completion: { value: val || 0 }
        });

        delete this.completion;
        this.children = this.children || [];

        this.children.push(newItem);

        return newItem;
    }
}

var model = {
    lists: [], //holds references to list roots
    createList: function(title) {
        var newList = Object.create(TodoProto, {
            text: { value: title },
            children: { value: [] }
        });

        this.lists.push(newList);

        return newList;
    },
    load: function() {
        //todo: load from cookies

        //temp
        var list = this.createList('Test');

        for (var i = 0; i != 20; ++i) {
            list.insert(i.toString());
        }

        return list;
    }
};
var TodoItem = React.createClass({displayName: "TodoItem",
    render: function() {
        return React.createElement("a", {className: "list-group-item", href: "#"},  this.props.model.text)
    }
});;

var Container = React.createClass({displayName: "Container",
    render: function() {
        var items = this.props.model.children.map(function(item) {
            return React.createElement(TodoItem, {model:  item },  item.text)
        });

        return(
            React.createElement("div", {className: "well my-well", id:  this.props.id}, 
                React.createElement("div", {className: "list-group"}, 
                     items 
                )
            )
        );
    }
});;

(function loadList() {
    var currentList = model.load();

    React.render(
        React.createElement(Container, {id: "well", model:  currentList }),
        $('#app')[0]
    );
})();

(function fillMenu() {
    var listMenu = '';
    model.lists.forEach(function(list) {
        listMenu += '<li><a href="#">' + list.text + '</a></li>';
    });
    listMenu += '<li class="divider" role="separator"></li>';
    
    $('#list-menu').prepend(listMenu);
})();